              </button>
            ) : (
              <button
                onClick={() => setShowLogin(true)}
                style={{
                  background: 'none',
                  border: 'none',
                  color: '#999',
                  cursor: 'pointer',
                  fontFamily: 'inherit',
                  fontSize: '0.875rem',
                  letterSpacing: '0.05em',
                  transition: 'color 0.2s ease'
