                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit',
                  fontSize: '1rem'
                }}
              />
              <button
                type="submit"
                style={{
                  padding: '0.75rem',
                  backgroundColor: '#000000',
                  color: '#ffffff',
                  border: 'none',
                  borderRadius: '2px',
                  cursor: 'pointer',
                  fontFamily: 'inherit',
                  fontSize: '1rem',
                  letterSpacing: '0.05em'
                }}
              >
                Login
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Upload Modal */}
      {showUpload && (
        <div
          style={{
            position: 'fixed',
            inset: 0,
            backgroundColor: 'rgba(0,0,0,0.8)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 200,
            backdropFilter: 'blur(5px)',
            overflowY: 'auto',
            padding: '2rem'
          }}
          onClick={() => {
            if (!uploading) {
              setShowUpload(false);
              uploadFiles.forEach(f => URL.revokeObjectURL(f.preview));
              setUploadFiles([]);
            }
          }}
        >
          <div
            style={{
              backgroundColor: '#ffffff',
              padding: '3rem',
              borderRadius: '2px',
              maxWidth: '900px',
              width: '90%',
              maxHeight: '90vh',
              overflowY: 'auto'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h2 style={{
              fontFamily: '"Cormorant Garamond", serif',
              fontSize: '1.5rem',
              marginBottom: '2rem',
              fontWeight: 300,
              letterSpacing: '0.05em',
              color: '#000000'
            }}>
              Upload Photos
            </h2>

            {/* Drag & Drop Zone */}
            {uploadFiles.length === 0 && (
              <div
                onDragEnter={handleDrag}
                onDragLeave={handleDrag}
                onDragOver={handleDrag}
                onDrop={handleDrop}
                style={{
                  border: `2px dashed ${dragActive ? '#1a1a1a' : '#ddd'}`,
                  borderRadius: '4px',
                  padding: '3rem',
                  textAlign: 'center',
                  backgroundColor: dragActive ? 'rgba(0,0,0,0.02)' : 'transparent',
                  transition: 'all 0.2s ease',
                  marginBottom: '1.5rem'
                }}
              >
                <p style={{ fontSize: '1.1rem', color: '#666', marginBottom: '1rem' }}>
                  Drag & drop images here
