              backdropFilter: 'blur(10px)'
            }}
          >

          </button>

          <button
            onClick={(e) => {
              e.stopPropagation();
              nextPhoto();
            }}
            style={{
              position: 'absolute',
              right: '2rem',
              top: '50%',
              transform: 'translateY(-50%)',
              background: 'rgba(255,255,255,0.1)',
              border: 'none',
              color: 'white',
              fontSize: '2rem',
              width: '3rem',
              height: '3rem',
              borderRadius: '50%',
              cursor: 'pointer',
              backdropFilter: 'blur(10px)'
            }}
          >

          </button>

          <button
            onClick={() => setShowLightbox(false)}
            style={{
              position: 'absolute',
              top: '2rem',
              right: '2rem',
              background: 'rgba(255,255,255,0.1)',
              border: 'none',
              color: 'white',
              fontSize: '1.5rem',
              width: '3rem',
              height: '3rem',
              borderRadius: '50%',
              cursor: 'pointer',
              backdropFilter: 'blur(10px)'
            }}
          >

          </button>

          <div
            style={{
              maxWidth: '90vw',
              maxHeight: '95vh',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: '2rem'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <img
              src={getFullUrl(filteredPhotos[currentPhotoIndex].cloudinary_url)}
              alt={filteredPhotos[currentPhotoIndex].title}
              style={{
                maxWidth: '100%',
                maxHeight: '85vh',
                objectFit: 'contain',
                borderRadius: '2px'
              }}
            />

            <div style={{ textAlign: 'center', color: 'white', maxWidth: '600px' }}>
              <h3 style={{
                fontFamily: '"Cormorant Garamond", serif',
                fontSize: '1.5rem',
                fontWeight: 300,
                marginBottom: '0.5rem',
                letterSpacing: '0.05em'
              }}>
                {filteredPhotos[currentPhotoIndex].title}
              </h3>

              {filteredPhotos[currentPhotoIndex].description && (
                <p style={{
                  fontSize: '1rem',
                  lineHeight: 1.6,
                  opacity: 0.8,
                  marginBottom: '1rem'
                }}>
                  {filteredPhotos[currentPhotoIndex].description}
                </p>
              )}

              {filteredPhotos[currentPhotoIndex].tags.length > 0 && (
                <div style={{
                  display: 'flex',
                  gap: '0.5rem',
                  flexWrap: 'wrap',
                  justifyContent: 'center'
                }}>
                  {filteredPhotos[currentPhotoIndex].tags.map(tag => (
                    <span
                      key={tag.id}
                      style={{
                        padding: '0.25rem 0.75rem',
                        backgroundColor: 'rgba(255,255,255,0.1)',
                        borderRadius: '20px',
                        fontSize: '0.875rem',
                        backdropFilter: 'blur(10px)'
                      }}
                    >
                      {tag.name}
                    </span>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PhotoPortfolio;
