import React, { useState, useEffect, useCallback } from 'react';
import { createClient } from '@supabase/supabase-js';

// Initialize Supabase client
const supabase = createClient(
  'https://ygftopwtblcoxgzusywy.supabase.co',
  'sb_publishable_dROTCxmlboYY-oj5UjwmPQ_KbP0MdlS'
);

// Cloudinary config
const CLOUDINARY_CLOUD_NAME = 'dgsr2qkwp';
const CLOUDINARY_UPLOAD_PRESET = 'portfolio_preset';

const PhotoPortfolio = () => {
  const [photos, setPhotos] = useState([]);
  const [allTags, setAllTags] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(false);
  const [showUpload, setShowUpload] = useState(false);
  const [showLightbox, setShowLightbox] = useState(false);
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [editingPhoto, setEditingPhoto] = useState(null);
  const [uploadPreview, setUploadPreview] = useState(null);
  const [uploadData, setUploadData] = useState({
    title: '',
    description: '',
    tags: '',
    file: null
  });

  // Check auth state
  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setUser(session?.user ?? null);
    });

    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null);
    });

    return () => subscription.unsubscribe();
  }, []);

  // Fetch photos
  const fetchPhotos = useCallback(async () => {
    const { data, error } = await supabase
      .from('photos')
      .select(`
        *,
        photo_tags(
          tags(id, name)
        )
      `)
      .order('created_at', { ascending: false });

    if (!error && data) {
      // Process photos to include tags array and remove duplicates
      const uniquePhotos = {};
      data.forEach(photo => {
        if (!uniquePhotos[photo.id]) {
          uniquePhotos[photo.id] = {
            ...photo,
            tags: photo.photo_tags?.map(pt => pt.tags).filter(Boolean) || []
          };
        }
      });

      const processedPhotos = Object.values(uniquePhotos);
      setPhotos(processedPhotos);

      // Extract unique tags
      const tagSet = new Set();
      processedPhotos.forEach(p => p.tags.forEach(t => tagSet.add(JSON.stringify(t))));
      setAllTags(Array.from(tagSet).map(t => JSON.parse(t)));
    }
  }, []);

  useEffect(() => {
    fetchPhotos();
  }, [fetchPhotos]);

  // Filter photos by selected tags
  const filteredPhotos = selectedTags.length > 0
    ? photos.filter(photo => 
        selectedTags.every(selectedTag => 
          photo.tags.some(tag => tag.id === selectedTag.id)
        )
      )
    : photos;

  // Auth handlers
  const handleLogin = async (e) => {
    e.preventDefault();
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    if (!error) {
      setShowLogin(false);
      setEmail('');
      setPassword('');
    } else {
      alert('Login failed: ' + error.message);
    }
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
  };

  // Tag filtering
  const toggleTag = (tag) => {
    setSelectedTags(prev => {
      const exists = prev.find(t => t.id === tag.id);
      if (exists) {
        return prev.filter(t => t.id !== tag.id);
      } else {
        return [...prev, tag];
      }
    });
  };

  // Upload handlers
  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setUploadData(prev => ({ ...prev, file }));
      const reader = new FileReader();
      reader.onloadend = () => setUploadPreview(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleUpload = async () => {
    if (!uploadData.file) return;

    try {
      // Upload to Cloudinary
      const formData = new FormData();
      formData.append('file', uploadData.file);
      formData.append('upload_preset', CLOUDINARY_UPLOAD_PRESET);

      const cloudinaryRes = await fetch(
        `https://api.cloudinary.com/v1_1/${CLOUDINARY_CLOUD_NAME}/image/upload`,
        { method: 'POST', body: formData }
      );

      const cloudinaryData = await cloudinaryRes.json();

      // Save to Supabase
      const { data: photoData, error: photoError } = await supabase
        .from('photos')
        .insert({
          cloudinary_url: cloudinaryData.secure_url,
          cloudinary_public_id: cloudinaryData.public_id,
          title: uploadData.title,
          description: uploadData.description,
          width: cloudinaryData.width,
          height: cloudinaryData.height
        })
        .select()
        .single();

      if (photoError) throw photoError;

      // Handle tags
      const tagNames = uploadData.tags.split(',').map(t => t.trim()).filter(Boolean);
      for (const tagName of tagNames) {
        // Get or create tag
        const { data: existingTag } = await supabase
          .from('tags')
          .select('id')
          .eq('name', tagName)
          .maybeSingle();

        let tagId;
        if (existingTag) {
          tagId = existingTag.id;
        } else {
          const { data: newTag } = await supabase
            .from('tags')
            .insert({ name: tagName })
            .select()
            .single();
          tagId = newTag.id;
        }

        // Link photo to tag
        await supabase
          .from('photo_tags')
          .insert({
            photo_id: photoData.id,
            tag_id: tagId
          });
      }

      // Reset and refresh
      setShowUpload(false);
      setUploadPreview(null);
      setUploadData({ title: '', description: '', tags: '', file: null });
      fetchPhotos();
    } catch (error) {
      alert('Upload failed: ' + error.message);
    }
  };

  // Delete photo
  const handleDelete = async (photo) => {
    if (!window.confirm('Delete this photo?')) return;

    try {
      // Delete from Supabase (cascade will handle photo_tags)
      // Note: This only deletes metadata. The image remains in Cloudinary.
      // For production, implement a backend API endpoint to securely delete from Cloudinary.
      await supabase.from('photos').delete().eq('id', photo.id);

      fetchPhotos();
    } catch (error) {
      alert('Delete failed: ' + error.message);
    }
  };

  // Edit photo
  const handleEdit = async () => {
    if (!editingPhoto) return;

    try {
      // Update photo metadata
      await supabase
        .from('photos')
        .update({
          title: editingPhoto.title,
          description: editingPhoto.description
        })
        .eq('id', editingPhoto.id);

      // Update tags
      // First, delete existing tags
      await supabase.from('photo_tags').delete().eq('photo_id', editingPhoto.id);

      // Then add new tags
      const tagNames = editingPhoto.tags.split(',').map(t => t.trim()).filter(Boolean);
      for (const tagName of tagNames) {
        const { data: existingTag } = await supabase
          .from('tags')
          .select('id')
          .eq('name', tagName)
          .maybeSingle();

        let tagId;
        if (existingTag) {
          tagId = existingTag.id;
        } else {
          const { data: newTag } = await supabase
            .from('tags')
            .insert({ name: tagName })
            .select()
            .single();
          tagId = newTag.id;
        }

        await supabase
          .from('photo_tags')
          .insert({
            photo_id: editingPhoto.id,
            tag_id: tagId
          });
      }

      setEditingPhoto(null);
      fetchPhotos();
    } catch (error) {
      alert('Update failed: ' + error.message);
    }
  };

  // Lightbox navigation
  const openLightbox = (index) => {
    setCurrentPhotoIndex(index);
    setShowLightbox(true);
  };

  const nextPhoto = () => {
    setCurrentPhotoIndex((prev) => (prev + 1) % filteredPhotos.length);
  };

  const prevPhoto = () => {
    setCurrentPhotoIndex((prev) => (prev - 1 + filteredPhotos.length) % filteredPhotos.length);
  };

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!showLightbox) return;
      if (e.key === 'ArrowRight') nextPhoto();
      if (e.key === 'ArrowLeft') prevPhoto();
      if (e.key === 'Escape') setShowLightbox(false);
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [showLightbox, currentPhotoIndex, filteredPhotos.length]);

  // Cloudinary URL builders
  const getThumbUrl = (url) => {
    return url.replace('/upload/', '/upload/w_600,h_600,c_fill,f_auto,q_auto/');
  };

  const getFullUrl = (url) => {
    return url.replace('/upload/', '/upload/w_2000,f_auto,q_auto/');
  };

  return (
    <div style={{
      fontFamily: '"Instrument Serif", "Crimson Pro", Georgia, serif',
      backgroundColor: '#fafaf8',
      minHeight: '100vh',
      color: '#1a1a1a'
    }}>
      {/* Header */}
      <header style={{
        position: 'sticky',
        top: 0,
        zIndex: 100,
        backgroundColor: 'rgba(250, 250, 248, 0.9)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(0,0,0,0.05)',
        padding: '1.5rem 2rem'
      }}>
        <div style={{
          maxWidth: '1600px',
          margin: '0 auto',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <h1 style={{
            fontFamily: '"Cormorant Garamond", "Playfair Display", serif',
            fontSize: '2rem',
            fontWeight: 300,
            letterSpacing: '0.05em',
            margin: 0
          }}>
            ATELIER
          </h1>

          <div style={{ display: 'flex', gap: '1.5rem', alignItems: 'center' }}>
            {user && (
              <button
                onClick={() => setShowUpload(true)}
                style={{
                  padding: '0.5rem 1.5rem',
                  backgroundColor: '#1a1a1a',
                  color: '#fafaf8',
                  border: 'none',
                  borderRadius: '2px',
                  cursor: 'pointer',
                  fontFamily: 'inherit',
                  fontSize: '0.875rem',
                  letterSpacing: '0.05em',
                  transition: 'all 0.2s ease'
                }}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#333'}
                onMouseLeave={(e) => e.target.style.backgroundColor = '#1a1a1a'}
              >
                + UPLOAD
              </button>
            )}

            {user ? (
              <button
                onClick={handleLogout}
                style={{
                  background: 'none',
                  border: 'none',
                  color: '#666',
                  cursor: 'pointer',
                  fontFamily: 'inherit',
                  fontSize: '0.875rem',
                  letterSpacing: '0.05em'
                }}
              >
                Logout
              </button>
            ) : (
              <button
                onClick={() => setShowLogin(true)}
                style={{
                  background: 'none',
                  border: 'none',
                  color: '#666',
                  cursor: 'pointer',
                  fontFamily: 'inherit',
                  fontSize: '0.875rem',
                  letterSpacing: '0.05em'
                }}
              >
                Login
              </button>
            )}
          </div>
        </div>
      </header>

      {/* Tag Filter Bar */}
      {allTags.length > 0 && (
        <div style={{
          padding: '2rem 2rem 1rem',
          borderBottom: '1px solid rgba(0,0,0,0.05)',
          backgroundColor: '#fafaf8'
        }}>
          <div style={{
            maxWidth: '1600px',
            margin: '0 auto',
            display: 'flex',
            gap: '0.75rem',
            flexWrap: 'wrap'
          }}>
            {allTags.map(tag => {
              const isSelected = selectedTags.some(t => t.id === tag.id);
              return (
                <button
                  key={tag.id}
                  onClick={() => toggleTag(tag)}
                  style={{
                    padding: '0.5rem 1rem',
                    backgroundColor: isSelected ? '#1a1a1a' : 'transparent',
                    color: isSelected ? '#fafaf8' : '#666',
                    border: `1px solid ${isSelected ? '#1a1a1a' : '#ddd'}`,
                    borderRadius: '20px',
                    cursor: 'pointer',
                    fontFamily: 'inherit',
                    fontSize: '0.875rem',
                    letterSpacing: '0.03em',
                    transition: 'all 0.2s ease'
                  }}
                >
                  {tag.name}
                </button>
              );
            })}
          </div>
        </div>
      )}

      {/* Masonry Gallery */}
      <main style={{
        maxWidth: '1600px',
        margin: '0 auto',
        padding: '3rem 2rem'
      }}>
        <div style={{
          columns: 'auto 300px',
          columnGap: '1.5rem'
        }}>
          {filteredPhotos.map((photo, index) => (
            <div
              key={photo.id}
              style={{
                breakInside: 'avoid',
                marginBottom: '1.5rem',
                position: 'relative',
                cursor: 'pointer',
                overflow: 'hidden',
                borderRadius: '2px',
                transition: 'transform 0.3s ease',
                backgroundColor: '#f5f5f3'
              }}
              onClick={() => openLightbox(index)}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'translateY(-4px)';
                if (user) {
                  e.currentTarget.querySelector('.admin-controls').style.opacity = '1';
                }
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'translateY(0)';
                if (user) {
                  e.currentTarget.querySelector('.admin-controls').style.opacity = '0';
                }
              }}
            >
              <img
                src={getThumbUrl(photo.cloudinary_url)}
                alt={photo.title}
                loading="lazy"
                style={{
                  width: '100%',
                  display: 'block',
                  objectFit: 'cover'
                }}
              />

              {user && (
                <div
                  className="admin-controls"
                  style={{
                    position: 'absolute',
                    top: '0.5rem',
                    right: '0.5rem',
                    display: 'flex',
                    gap: '0.5rem',
                    opacity: 0,
                    transition: 'opacity 0.2s ease'
                  }}
                  onClick={(e) => e.stopPropagation()}
                >
                  <button
                    onClick={() => setEditingPhoto({
                      ...photo,
                      tags: photo.tags.map(t => t.name).join(', ')
                    })}
                    style={{
                      padding: '0.5rem',
                      backgroundColor: 'rgba(255,255,255,0.95)',
                      border: 'none',
                      borderRadius: '2px',
                      cursor: 'pointer',
                      fontSize: '0.875rem'
                    }}
                  >

                  </button>
                  <button
                    onClick={() => handleDelete(photo)}
                    style={{
                      padding: '0.5rem',
                      backgroundColor: 'rgba(255,255,255,0.95)',
                      border: 'none',
                      borderRadius: '2px',
                      cursor: 'pointer',
                      fontSize: '0.875rem'
                    }}
                  >

                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </main>

      {/* Login Modal */}
      {showLogin && (
        <div
          style={{
            position: 'fixed',
            inset: 0,
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 200,
            backdropFilter: 'blur(5px)'
          }}
          onClick={() => setShowLogin(false)}
        >
          <div
            style={{
              backgroundColor: '#fafaf8',
              padding: '3rem',
              borderRadius: '2px',
              maxWidth: '400px',
              width: '90%'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h2 style={{
              fontFamily: '"Cormorant Garamond", serif',
              fontSize: '1.5rem',
              marginBottom: '2rem',
              fontWeight: 300,
              letterSpacing: '0.05em'
            }}>
              Admin Login
            </h2>

            <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit',
                  fontSize: '1rem'
                }}
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit',
                  fontSize: '1rem'
                }}
              />
              <button
                type="submit"
                style={{
                  padding: '0.75rem',
                  backgroundColor: '#1a1a1a',
                  color: '#fafaf8',
                  border: 'none',
                  borderRadius: '2px',
                  cursor: 'pointer',
                  fontFamily: 'inherit',
                  fontSize: '1rem',
                  letterSpacing: '0.05em'
                }}
              >
                Login
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Upload Modal */}
      {showUpload && (
        <div
          style={{
            position: 'fixed',
            inset: 0,
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 200,
            backdropFilter: 'blur(5px)',
            overflowY: 'auto'
          }}
          onClick={() => {
            setShowUpload(false);
            setUploadPreview(null);
            setUploadData({ title: '', description: '', tags: '', file: null });
          }}
        >
          <div
            style={{
              backgroundColor: '#fafaf8',
              padding: '3rem',
              borderRadius: '2px',
              maxWidth: '600px',
              width: '90%',
              maxHeight: '90vh',
              overflowY: 'auto'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h2 style={{
              fontFamily: '"Cormorant Garamond", serif',
              fontSize: '1.5rem',
              marginBottom: '2rem',
              fontWeight: 300,
              letterSpacing: '0.05em'
            }}>
              Upload Photo
            </h2>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
              <input
                type="file"
                accept="image/*"
                onChange={handleFileSelect}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit'
                }}
              />

              {uploadPreview && (
                <img
                  src={uploadPreview}
                  alt="Preview"
                  style={{
                    width: '100%',
                    borderRadius: '2px',
                    maxHeight: '300px',
                    objectFit: 'contain'
                  }}
                />
              )}

              <input
                type="text"
                placeholder="Title"
                value={uploadData.title}
                onChange={(e) => setUploadData(prev => ({ ...prev, title: e.target.value }))}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit',
                  fontSize: '1rem'
                }}
              />

              <textarea
                placeholder="Description"
                value={uploadData.description}
                onChange={(e) => setUploadData(prev => ({ ...prev, description: e.target.value }))}
                rows={3}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit',
                  fontSize: '1rem',
                  resize: 'vertical'
                }}
              />

              <input
                type="text"
                placeholder="Tags (comma-separated)"
                value={uploadData.tags}
                onChange={(e) => setUploadData(prev => ({ ...prev, tags: e.target.value }))}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit',
                  fontSize: '1rem'
                }}
              />

              <button
                onClick={handleUpload}
                disabled={!uploadData.file}
                style={{
                  padding: '0.75rem',
                  backgroundColor: uploadData.file ? '#1a1a1a' : '#ccc',
                  color: '#fafaf8',
                  border: 'none',
                  borderRadius: '2px',
                  cursor: uploadData.file ? 'pointer' : 'not-allowed',
                  fontFamily: 'inherit',
                  fontSize: '1rem',
                  letterSpacing: '0.05em'
                }}
              >
                Upload
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Modal */}
      {editingPhoto && (
        <div
          style={{
            position: 'fixed',
            inset: 0,
            backgroundColor: 'rgba(0,0,0,0.5)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 200,
            backdropFilter: 'blur(5px)'
          }}
          onClick={() => setEditingPhoto(null)}
        >
          <div
            style={{
              backgroundColor: '#fafaf8',
              padding: '3rem',
              borderRadius: '2px',
              maxWidth: '600px',
              width: '90%'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h2 style={{
              fontFamily: '"Cormorant Garamond", serif',
              fontSize: '1.5rem',
              marginBottom: '2rem',
              fontWeight: 300,
              letterSpacing: '0.05em'
            }}>
              Edit Photo
            </h2>

            <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
              <input
                type="text"
                placeholder="Title"
                value={editingPhoto.title}
                onChange={(e) => setEditingPhoto(prev => ({ ...prev, title: e.target.value }))}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit',
                  fontSize: '1rem'
                }}
              />

              <textarea
                placeholder="Description"
                value={editingPhoto.description}
                onChange={(e) => setEditingPhoto(prev => ({ ...prev, description: e.target.value }))}
                rows={3}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit',
                  fontSize: '1rem',
                  resize: 'vertical'
                }}
              />

              <input
                type="text"
                placeholder="Tags (comma-separated)"
                value={editingPhoto.tags}
                onChange={(e) => setEditingPhoto(prev => ({ ...prev, tags: e.target.value }))}
                style={{
                  padding: '0.75rem',
                  border: '1px solid #ddd',
                  borderRadius: '2px',
                  fontFamily: 'inherit',
                  fontSize: '1rem'
                }}
              />

              <button
                onClick={handleEdit}
                style={{
                  padding: '0.75rem',
                  backgroundColor: '#1a1a1a',
                  color: '#fafaf8',
                  border: 'none',
                  borderRadius: '2px',
                  cursor: 'pointer',
                  fontFamily: 'inherit',
                  fontSize: '1rem',
                  letterSpacing: '0.05em'
                }}
              >
                Save Changes
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Lightbox */}
      {showLightbox && filteredPhotos[currentPhotoIndex] && (
        <div
          style={{
            position: 'fixed',
            inset: 0,
            backgroundColor: 'rgba(0,0,0,0.95)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 300,
            padding: '2rem'
          }}
          onClick={() => setShowLightbox(false)}
        >
          <button
            onClick={(e) => {
              e.stopPropagation();
              prevPhoto();
            }}
            style={{
              position: 'absolute',
              left: '2rem',
              top: '50%',
              transform: 'translateY(-50%)',
              background: 'rgba(255,255,255,0.1)',
              border: 'none',
              color: 'white',
              fontSize: '2rem',
              width: '3rem',
              height: '3rem',
              borderRadius: '50%',
              cursor: 'pointer',
              backdropFilter: 'blur(10px)'
            }}
          >

          </button>

          <button
            onClick={(e) => {
              e.stopPropagation();
              nextPhoto();
            }}
            style={{
              position: 'absolute',
              right: '2rem',
              top: '50%',
              transform: 'translateY(-50%)',
              background: 'rgba(255,255,255,0.1)',
              border: 'none',
              color: 'white',
              fontSize: '2rem',
              width: '3rem',
              height: '3rem',
              borderRadius: '50%',
              cursor: 'pointer',
              backdropFilter: 'blur(10px)'
            }}
          >

          </button>

          <button
            onClick={() => setShowLightbox(false)}
            style={{
              position: 'absolute',
              top: '2rem',
              right: '2rem',
              background: 'rgba(255,255,255,0.1)',
              border: 'none',
              color: 'white',
              fontSize: '1.5rem',
              width: '3rem',
              height: '3rem',
              borderRadius: '50%',
              cursor: 'pointer',
              backdropFilter: 'blur(10px)'
            }}
          >

          </button>

          <div
            style={{
              maxWidth: '90vw',
              maxHeight: '80vh',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: '2rem'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <img
              src={getFullUrl(filteredPhotos[currentPhotoIndex].cloudinary_url)}
              alt={filteredPhotos[currentPhotoIndex].title}
              style={{
                maxWidth: '100%',
                maxHeight: '70vh',
                objectFit: 'contain',
                borderRadius: '2px'
              }}
            />

            <div style={{ textAlign: 'center', color: 'white', maxWidth: '600px' }}>
              <h3 style={{
                fontFamily: '"Cormorant Garamond", serif',
                fontSize: '1.5rem',
                fontWeight: 300,
                marginBottom: '0.5rem',
                letterSpacing: '0.05em'
              }}>
                {filteredPhotos[currentPhotoIndex].title}
              </h3>

              {filteredPhotos[currentPhotoIndex].description && (
                <p style={{
                  fontSize: '1rem',
                  lineHeight: 1.6,
                  opacity: 0.8,
                  marginBottom: '1rem'
                }}>
                  {filteredPhotos[currentPhotoIndex].description}
                </p>
              )}

              {filteredPhotos[currentPhotoIndex].tags.length > 0 && (
                <div style={{
                  display: 'flex',
                  gap: '0.5rem',
                  flexWrap: 'wrap',
                  justifyContent: 'center'
                }}>
                  {filteredPhotos[currentPhotoIndex].tags.map(tag => (
                    <span
                      key={tag.id}
                      style={{
                        padding: '0.25rem 0.75rem',
                        backgroundColor: 'rgba(255,255,255,0.1)',
                        borderRadius: '20px',
                        fontSize: '0.875rem',
                        backdropFilter: 'blur(10px)'
                      }}
                    >
                      {tag.name}
                    </span>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PhotoPortfolio;
